mapper.select:
SELECT 

FROM star_photo a

WHERE a.id = #{id}

SELECT 

FROM star_photo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.star_id = #{starId}


		AND a.type = #{type}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM star_photo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE star_photo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
