mapper.select:
SELECT 

FROM star_song a

WHERE a.id = #{id}

SELECT
FROM star_song a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM star_song a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE star_song SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
